import React, { createContext ,useState} from 'react';
import { Termekek } from '../Termekek';

export const EtlapContext = createContext(null);

const getDefaultRendeles = () => {
        let rendeles = {}
        for (let i = 1; i < Termekek.length+  1; i++) {
            rendeles[i] = 0
        }
        return rendeles;
};


export const EtlapContextProvider = (props) => {
   const  [rendelesItems, setRendelesItems] = useState();
    
   const addToRendeles = (itemId) => {
    setRendelesItems((prev) => ({ ...prev, [itemId]: prev[itemId] + 1 }));
   };
   
   const removeFromRendeles = (itemId) => {
    setRendelesItems((prev) => ({ ...prev, [itemId]: prev[itemId] - 1 }));
   };
   
  const contextValue = {rendelesItems, addToRendeles, removeFromRendeles};
  
  
   return (
    <EtlapContext.Provider value={contextValue}>
        {props.children}
    </EtlapContext.Provider>
  );
};
