import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import './Navbar.css';
import LoginSignup from '../Oldalak/LoginSignup';
import Modal from 'react-modal';

Modal.setAppElement('#root'); // Ez a sor szükséges a modális ablak megfelelő működéséhez

const Navbar = () => {
    
  const [menu, setMenu] = useState("Főoldal");
  const [showLogin, setShowLogin] = useState(false); // Állapot a bejelentkezési űrlap megjelenítésének kezelésére
  const [search, setSearch] = useState(""); // Új állapot a keresősáv szövegének kezelésére
  
  const loginRef = useRef(null);
 

  const toggleLogin = () => {
    setShowLogin(!showLogin);
  };

  const handleMenuClick = (menuName) => {
    setMenu(menuName);
    setSearch(menuName); // Frissíti a keresősáv szövegét
  };

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (loginRef.current && !loginRef.current.contains(event.target)) {
        setShowLogin(false); // Elrejti az űrlapot, ha a kattintás az űrlapon kívül történt
      }
    };

    const handleKeyPress = (event) => {
      if (event.key === "Escape") {
        setShowLogin(false); // Elrejti az űrlapot, ha az ESC gombot megnyomják
      }
    };

    if (showLogin) {
      document.addEventListener('mousedown', handleClickOutside);
      document.addEventListener('keydown', handleKeyPress);
    } else {
      document.removeEventListener('mousedown', handleClickOutside);
      document.removeEventListener('keydown', handleKeyPress);
    }

    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
      document.removeEventListener('keydown', handleKeyPress);
    };
  }, [showLogin]);

  return (
    <div className="navbar" style={{position: 'fixed', width: '100%'}}>
      <button className="logo-button" onClick={() =>  handleMenuClick('/')}>
    <h1 className='logo'>Etterem</h1>
      </button>
      <ul className='nav-menu'>
      <li onClick={() => handleMenuClick("Főoldal")}> <Link to='/'></Link>
          Főoldal{menu === "Főoldal" && <hr />}
        </li>
       
    <li
      onClick={() => handleMenuClick("Menü")}
     
    > <Link to='/menu'></Link>
      Menü{menu === "Menü" && <hr />}
    </li>
    <li
      onClick={() => handleMenuClick("Rendelés")}
     
    > <Link to='/rendeles'></Link>
      Rendelés{menu === "Rendelés" && <hr />}
    </li>
    <li
      onClick={() => handleMenuClick("Kapcsolat")}
     
    > <Link to='/kapcsolat'></Link>
      Kapcsolat{menu === "Kapcsolat" && <hr />}
    </li>
      </ul>
      <div className='nav-login'>
        <button onClick={toggleLogin}> <Link to='/login'></Link>  Login</button> 
      </div>
      <input type="text" value={search} readOnly /> {/* Keresősáv hozzáadása */}
      <Modal 
  isOpen={showLogin} 
  onRequestClose={() => setShowLogin(false)}
  style={{
    content: {
      width: '1%',
      height: '1%',
      border: 'none',
   }
}}
>
   <LoginSignup ref={loginRef} />
</Modal>
    </div>
  );
};

export default Navbar;
