import React, { useContext } from 'react';
import EtlapKontextus from '../../Kontextus/Etlap-kontextus';
import { Termekek } from '../../Termekek';

const Rendeles = () => {
  const { rendelesItems } = useContext(EtlapKontextus);

  return (
    <div className='Rendeles'>
      <h2>Rendelés</h2>
      {Termekek.map((termek) => {
        const rendelesItemAmount = rendelesItems[termek.id] || 0;
        if (rendelesItemAmount > 0) {
          return (
            <div key={termek.id}>
              <h3>{termek.termekNev}</h3>
              <p>Mennyiség: {rendelesItemAmount}</p>
              <p>Ár: {termek.ar * rendelesItemAmount}</p>
            </div>
          );
        }
        return null;
      })}
    </div>
  );
};

export default Rendeles;
