import React, { useContext, useEffect } from "react";
import axios from 'axios';
import { ShopContext } from "../../context/shop-context";
import { PRODUCTS } from "../../products";
import { CartItem } from "./cart-item";
import { useNavigate } from "react-router-dom";
import "./CSS/cart.css";

const Cart = () => {
    const { cartItems, getTotalCartAmount, checkout } = useContext(ShopContext);
    const totalAmount = getTotalCartAmount();

    const navigate = useNavigate();

    // Törlés gombra kattintva töröljük a backend szerveren tárolt adatokat
    const handleDelete = async (id) => {
        try {
            await axios.delete(`/api/cart/${id}`);
        } finally {
            checkout();
            navigate("");
        }
    };

    // Az oldal betöltődésekor lekérjük a termék adatait
    useEffect(() => {
        const fetchProduct = async (id) => {
            try {
                const response = await axios.get(`/api/products/${id}`);
                console.log(response.data);
            } catch (error) {
                console.error(`Error fetching product with id ${id}`, error);
            }
        };

        for (const id in cartItems) {
            fetchProduct(id);
        }
    }, [cartItems]);

    return (
        <div className="cart">
            <div className="cartTitle">
                <h1>Kosár tartalma:</h1>
            </div>
            <div className="cart">
                {PRODUCTS.map((product) => {
                    if (cartItems[product.id] !== 0) {
                        return (
                            <CartItem 
                                key={product.id} 
                                data={product} 
                                onDelete={() => handleDelete(product.id)}
                            />
                        );
                    } else {
                        return null;
                    }
                })}
            </div>

            {totalAmount > 0 ? (
                <div className="checkout">
                    <p> Végösszeg: {totalAmount}Ft </p>
                    <button onClick={() => navigate("/shop")}>Vásárlás folytatása</button>
                    <button onClick={() => handleDelete()}> Törlés </button>
                </div>
            ) : (
                <h1> A kosarad üres!</h1>
            )}
        </div>
    );
};

export default Cart;
