import React, { createContext } from "react";
import { Termekek } from "../Termekek";
import { useState } from "react";

export const EtlapKontextus = createContext(null);




const getDefaultRendeles = () => {
    let rendeles = {}
    for (let i = 1; i < Termekek.length + 1; i++) {
        rendeles[i] = 0
        
    }
  return rendeles;
};



export const EtlapKontextusEllato = (props) => {
        
    const [rendelesItems, setRendelesItems] = useState(getDefaultRendeles());
    
    const addToRendeles = (itemId) => {
        
        setRendelesItems((prev) =>({...prev, [itemId]: prev[itemId] + 1})); 
    };

    const removeFromRendeles = (itemId) => {
        
        setRendelesItems((prev) =>({...prev, [itemId]: prev[itemId] - 1})); 
    };

    const contextValue = {setrendelesItems, addToRendeles, removeFromRendeles};

    return (<EtlapKontextusEllato value={contextValue}>
        {props.children}
        </EtlapKontextusEllato>
    
); 
    
};
