import React, { useContext } from "react";
import { ShopContext } from "../../context/shop-context";
import axios from 'axios'; // Ne felejtsd el telepíteni az axios csomagot!

export const Product = (props) => {
  const { id, productName, price, productImage } = props.data;
  const { addToCart, cartItems } = useContext(ShopContext);

  const cartItemCount = cartItems[id];

  const handleAddToCart = async (id) => {
    // A POST kérés elküldése a backend szervernek
    const response = await axios.post('http://your-backend-server.com/api/path', {
      name: productName,
      price: price,
      weight: 0 // Itt állítsd be a termék súlyát
    });

    // Ellenőrizd a választ és hozzáadod a terméket a kosárhoz
    if (response.status === 200) {
      addToCart(id);
    }
  };

  return (
    <div className="product">
      <img src={productImage} alt="product"/>
      <div className="description">
        <p>
          <b>{productName}</b>
        </p>
        <p> {price} Ft</p>
      </div>
      <button className="addToCartBttn" onClick={() => handleAddToCart(id)}>
       Hozzáadva a kosárhoz{cartItemCount > 0 && <> ({cartItemCount})</>}
      </button>
    </div>
  );
};
