import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBars } from '@fortawesome/free-solid-svg-icons'; // Importáljuk a szükséges ikont
import './Navbar.css';

const Navbar = () => {
  const [menu, setMenu] = useState("");
  const [isOpen, setIsOpen] = useState(false); // Új állapot a menü nyitottságának kezelésére

  const navigate = useNavigate();

  const handleMenuClick = (menuName, path) => {
    setMenu(menuName);
    navigate(path);
    setIsOpen(false); // Bezárjuk a menüt, ha a felhasználó rákattintott egy menüpontra
  };

  return (
    <div className="navbar" style={{position: 'fixed', width: '100%'}}>
      <button className="logo-button" onClick={() =>  {handleMenuClick('Főoldal', '/fooldal'); window.location.reload();}}>
        <h1 className='logo'>FoodRapid</h1>
      </button>
      <div className='nav-signup'>
        <button onClick={() => handleMenuClick("Regisztráció", '/signup')}>
          Regisztráció
        </button> 
      </div>
      <div className='nav-login'>
        <button onClick={() => handleMenuClick("Bejelentkezés", '/login')}>
          Bejelentkezés
        </button> 
      </div>
      <FontAwesomeIcon icon={faBars} size="2x" color='#260de3' onClick={() => setIsOpen(!isOpen)} /> {/* Hozzáadtuk az onClick eseménykezelőt */}
      {isOpen && ( // Csak akkor jelenítjük meg a menüt, ha az isOpen állapot igaz
        <ul className='nav-menu'>
          <li onMouseOver={() => setMenu("Főoldal")} onMouseOut={() => setMenu("")} onClick={() => handleMenuClick("Főoldal", '/fooldal')}>
            <Link style={{textDecoration: 'none', color: 'black'}} to='/fooldal'>Főoldal</Link>
            {menu === "Főoldal" && <hr />}
          </li>
          <li onMouseOver={() => setMenu("Étlap")} onMouseOut={() => setMenu("")} onClick={() => handleMenuClick("Étlap", '/etlap')}>
            <Link style={{textDecoration: 'none' , color: 'black' }} to='/etlap'>Étlap</Link>
            {menu === "Étlap" && <hr />}
          </li>
          <li onMouseOver={() => setMenu("Kapcsolat")} onMouseOut={() => setMenu("")} onClick={() => handleMenuClick("Kapcsolat", '/kapcsolat')}>
            <Link style={{textDecoration: 'none' , color: 'black'}} to='/kapcsolat'>Kapcsolat</Link>
            {menu === "Kapcsolat" && <hr />}
          </li>
        </ul>
      )}
    </div>
  );
};

export default Navbar;
