const createFood = async (food) => {
  try {
    const response = await axios.post('https://localhost:7208/foods', {
      name: food.name,
      price: food.price,
      weight: food.weight
    });
    console.log(response);
  } catch (error) {
    console.error('Hiba történt!', error);
  }
};

// READ ALL (GET)
const getAllFoods = async () => {
  try {
    const response = await axios.get('https://localhost:7208/foods');
    console.log(response);
  } catch (error) {
    console.error('Hiba történt!', error);
  }
};

// READ ONE (GET)
const getFoodById = async (id) => {
  try {
    const response = await axios.get(`https://localhost:7208/foods${id}`);
    console.log(response);
  } catch (error) {
    console.error('Hiba történt!', error);
  }
};

// UPDATE (PUT)
const updateFood = async (id, updatedFood) => {
  try {
    const response = await axios.put(`/foods/${id}`, updatedFood);
    console.log(response);
  } catch (error) {
    console.error('Hiba történt!', error);
  }
};

// DELETE
const deleteFood = async (id) => {
  try {
    const response = await axios.delete(`/foods/${id}`);
    console.log('Törölve');
  } catch (error) {
    console.error('Hiba történt!', error);
  }
};

export const Product = (props) => {
  const { id, weight, name, price, productImage } = props.data;
  const { addToCart, cartItems } = useContext(ShopContext);

  const cartItemCount = cartItems[id];

  // Use useEffect to call the getFoodById function when the component mounts
