import React, { useState, useEffect, useRef } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Navbar.css';
import LoginSignup from '../Oldalak/LoginSignup';
import Modal from 'react-modal';

Modal.setAppElement('#root');

const Navbar = () => {
  const [menu, setMenu] = useState("Főoldal");
  const [showLogin, setShowLogin] = useState(false);
  
  const loginRef = useRef(null);
  const navigate = useNavigate();

  const handleMenuClick = (menuName, path) => {
    setMenu(menuName);
    navigate(path);
  };

  const toggleLogin = () => {
    setShowLogin(!showLogin);
  };

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (loginRef.current && !loginRef.current.contains(event.target)) {
        setShowLogin(false);
      }
    };

    const handleKeyPress = (event) => {
      if (event.key === "Escape") {
        setShowLogin(false);
      }
    };

    if (showLogin) {
      document.addEventListener('mousedown', handleClickOutside);
      document.addEventListener('keydown', handleKeyPress);
    } else {
      document.removeEventListener('mousedown', handleClickOutside);
      document.removeEventListener('keydown', handleKeyPress);
    }

    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
      document.removeEventListener('keydown', handleKeyPress);
    };
  }, [showLogin]);

  return (
    <div className="navbar" style={{position: 'fixed', width: '100%'}}>
      <button className="logo-button" onClick={() =>  {handleMenuClick('Főoldal', '/fooldal'); window.location.reload();}}>
        <h1 className='logo'>Etterem</h1>
      </button>
      <ul className='nav-menu'>
        <li onClick={() => handleMenuClick("Főoldal", '/fooldal')}>
          <Link style={{textDecoration: 'none', color: 'black'}} to='/fooldal'>Főoldal</Link>
          {menu === "Főoldal" && <hr />}
        </li>
        <li onClick={() => handleMenuClick("Menü", '/menu')}>
          <Link style={{textDecoration: 'none' , color: 'black' }} to='/menu'>Menü</Link>
          {menu === "Menü" && <hr />}
        </li>
        <li onClick={() => handleMenuClick("Rendelés", '/rendeles')}>
          <Link style={{textDecoration: 'none' , color: 'black' }} to='/rendeles'>Rendelés</Link>
          {menu === "Rendelés" && <hr />}
        </li>
        <li onClick={() => handleMenuClick("Kapcsolat", '/kapcsolat')}>
          <Link style={{textDecoration: 'none' , color: 'black'}} to='/kapcsolat'>Kapcsolat</Link>
          {menu === "Kapcsolat" && <hr />}
        </li>
      </ul>
      <div className='nav-login'>
        <button onClick={toggleLogin}>
          Bejelentkezés/Regisztráció
        </button> 
      </div>
      <Modal 
        isOpen={showLogin} 
        onRequestClose={() => setShowLogin(false)}
        style={{
          content: {
            width: '1%',
            height: '1%',
            border: 'none',
          }
        }}
      >
        <LoginSignup ref={loginRef} />
      </Modal>
    </div>
  );
};

export default Navbar;
