import React, { useState, useEffect, useContext } from "react";
import axios from "axios";
import { ShopContext } from "../../context/shop-context";

export const Product = (props) => {
  const { id, weight, name, price, productImage } = props.data;
  const { addToCart, cartItems } = useContext(ShopContext);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [foodDetails, setFoodDetails] = useState(null);

  useEffect(() => {
    fetchFoodDetails();
  }, []);

  const fetchFoodDetails = async () => {
    setIsLoading(true);
    try {
      const response = await axios.get(`/foods/${id}`);
      setFoodDetails(response.data.obj);
    } catch (error) {
      setError(error.response.data.message);
    }
    setIsLoading(false);
  };

  const handleAddToCart = () => {
    addToCart(id);
  };

  const cartItemCount = cartItems[id] || 0;

  return (
    <div className="product">
      {isLoading && <p>Loading...</p>}
      {error && <p>Error: {error}</p>}
      {foodDetails && (
        <>
          <img src={productImage} alt="product" />
          <div className="description">
            <p>
              <b>{foodDetails.name}</b>
            </p>
            <p>{foodDetails.price} Ft</p>
            <p>{foodDetails.weight}</p>
          </div>
          <button className="addToCartBttn" onClick={handleAddToCart}>
            Hozzáadva a kosárhoz{cartItemCount > 0 && <> ({cartItemCount})</>}
          </button>
        </>
      )}
    </div>
  );
};
