import React, { useContext } from 'react'
import EtlapKontextus from '../../Kontextus/Etlap-kontextus';

const Termek = (props) => {
  const {id,termekNev,ar,termekKep} = props.data;
  const {addToRendeles,rendelesItems} = useContext(EtlapKontextus);
  
  const rendelesItemAmount = rendelesItems[id] || 0; 
  return (
    <div className='termek'>
      <img src={termekKep}/>
      <div className='leiras'>
           <p>
            <b>{termekNev}</b>
           </p>
            <p>{ar}</p>
            
      </div>
      <button className='addTocartBttn' onClick={() => addToRendeles(id)}>
        Hozzáadás a rendeléshez {rendelesItemAmount > 0 && <> ({rendelesItemAmount})</>}}
      </button>
    </div>
  )
}

export default Termek
