import React, { useState, useEffect, useRef } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Navbar, Nav } from 'react-bootstrap'; // Importáljuk a szükséges komponenseket
import './Navbar.css';
import LoginSignup from '../Oldalak/LoginSignup';
import Modal from 'react-modal';

Modal.setAppElement('#root');

const MyNavbar = () => {
  const [menu, setMenu] = useState("Főoldal");
  const [showLogin, setShowLogin] = useState(false);
  
  const loginRef = useRef(null);
  const navigate = useNavigate();

  const handleMenuClick = (menuName, path) => {
    setMenu(menuName);
    navigate(path);
  };

  const toggleLogin = () => {
    setShowLogin(!showLogin);
  };

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (loginRef.current && !loginRef.current.contains(event.target)) {
        setShowLogin(false);
      }
    };

    const handleKeyPress = (event) => {
      if (event.key === "Escape") {
        setShowLogin(false);
      }
    };

    if (showLogin) {
      document.addEventListener('mousedown', handleClickOutside);
      document.addEventListener('keydown', handleKeyPress);
    } else {
      document.removeEventListener('mousedown', handleClickOutside);
      document.removeEventListener('keydown', handleKeyPress);
    }

    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
      document.removeEventListener('keydown', handleKeyPress);
    };
  }, [showLogin]);

  return (
    <Navbar bg="light" expand="lg">
      <Navbar.Brand onClick={() =>  {handleMenuClick('Főoldal', '/fooldal'); window.location.reload();}}>Etterem</Navbar.Brand>
      <Navbar.Toggle aria-controls="basic-navbar-nav" />
      <Navbar.Collapse id="basic-navbar-nav">
        <Nav className="mr-auto">
          <Nav.Link onClick={() => handleMenuClick("Főoldal", '/fooldal')}><Link style={{textDecoration: 'none', color: 'black'}} to='/fooldal'>Főoldal</Link></Nav.Link>
          <Nav.Link onClick={() => handleMenuClick("Menü", '/menu')}><Link style={{textDecoration: 'none' , color: 'black' }} to='/menu'>Menü</Link></Nav.Link>
          <Nav.Link onClick={() => handleMenuClick("Rendelés", '/rendeles')}><Link style={{textDecoration: 'none' , color: 'black' }} to='/rendeles'>Rendelés</Link></Nav.Link>
          <Nav.Link onClick={() => handleMenuClick("Kapcsolat", '/kapcsolat')}><Link style={{textDecoration: 'none' , color: 'black'}} to='/kapcsolat'>Kapcsolat</Link></Nav.Link>
        </Nav>
        <Nav>
          <Nav.Link onClick={toggleLogin}>Login/Signup</Nav.Link>
        </Nav>
      </Navbar.Collapse>
      <Modal 
        isOpen={showLogin} 
        onRequestClose={() => setShowLogin(false)}
        style={{
          content: {
            width: '1%',
            height: '1%',
            border: 'none',
          }
        }}
      >
        <LoginSignup ref={loginRef} />
      </Modal>
    </Navbar>
  );
};

export default MyNavbar;
