import React, { useContext, useState } from "react";
import { ShopContext } from "../../context/shop-context";
import axios from 'axios'; 

export const Product = (props) => {
  const { id, weight, name, price, productImage } = props.data;
  const { addToCart, cartItems } = useContext(ShopContext);

  // useState hozzáadása a változókhoz
  const [productName, setProductName] = useState(name);
  const [productPrice, setProductPrice] = useState(price);
  const [productWeight, setProductWeight] = useState(weight);

  const cartItemCount = cartItems[id];

  const handleAddToCart = async (id) => {
    try {
      const response = await axios.post('https://localhost:7208/foods', { 
        name: productName,
        price: productPrice,
        weight: productWeight,
      });

      // Ellenőrizd a választ és hozzáadod a terméket a kosárhoz
      if (response.status === 200) {
        addToCart(id);
      }
    } catch (error) {
      console.error('Hiba történt a termék hozzáadása közben:', error);
    }
  };

  return (
    <div className="product">
      <img src={productImage} alt="product"/>
      <div className="description">
        <p>
          <b>{productName}</b>
        </p>
        <p> {productPrice} Ft</p>
        <p> {productWeight}</p>
      </div>
      <button className="addToCartBttn" onClick={() => handleAddToCart(id)}>
       Hozzáadva a kosárhoz{cartItemCount > 0 && <> ({cartItemCount})</>}
      </button>
    </div>
  );
};
